/* Holy Weapon Macro */
const targetToken = (typeof target !== "undefined" && target) ? target : Array.from(game.user.targets)[0];

if (!targetToken) { 
    ui.notifications.warn("To cast Holy Weapon, you must target a creature!"); 
    return; 
}

const sdxModule = game.modules.get("shadowdark-extras");
if (sdxModule.api.showHolyWeaponDialog) {
    // Get critical success status from the spell cast context
    const isCritical = args.isCritical ?? false;

    await sdxModule.api.showHolyWeaponDialog(
        actor,                      // casterActor
        item,                       // casterItem (the Holy Weapon spell)
        targetToken.actor,          // targetActor
        targetToken,                // targetToken
        args.originatingUserId,     // originatingUserId (for GM routing)
        isCritical                  // isCritical - grants +2/+2 on critical success
    );
}

// Cleansing Weapon
const targetToken = (typeof target !== "undefined" && target) ? target : Array.from(game.user.targets)[0];
if (!targetToken) { 
    ui.notifications.warn("You must target a creature!"); 
    return; 
}

const sdxModule = game.modules.get("shadowdark-extras");

// Get critical success status from the spell cast context
const isCritical = args.isCritical ?? false;

const userId = (typeof originatingUserId !== "undefined") ? originatingUserId : undefined;
await sdxModule.api.showCleansingWeaponDialog(
    actor,                      // casterActor
    item,                       // casterItem (the Cleansing Weapon spell)
    targetToken.actor,          // targetActor
    targetToken,                // targetToken
    userId,                     // originatingUserId (for GM routing)
    isCritical                  // isCritical - grants 2d4/2d6 on critical success
);

// Identify
const targetToken = (typeof target !== "undefined" && target) ? target : game.user.targets.first();
const targetActor = targetToken?.actor || item.actor;

const sdxModule = game.modules.get("shadowdark-extras");

const unidentifiedItems = targetActor.items.filter(i => 
    sdxModule.api.isUnidentified(i)
);

if (unidentifiedItems.length === 0) {
    ui.notifications.warn(game.i18n.localize("SHADOWDARK_EXTRAS.identify.noItems"));
    return;
}

const userId = (typeof originatingUserId !== "undefined") ? originatingUserId : undefined;
await sdxModule.api.showIdentifyDialog(targetActor, unidentifiedItems, item, userId);

// Turn Undead Macro
// Run on Success

// 1. Get Setup Data
const casterToken = token;
const casterActor = actor;

// Ensure spellCheckResult is a number
const spellCheckResult = Number(args.rollResult);

if (!casterToken || !casterActor || isNaN(spellCheckResult)) {
    ui.notifications.warn("Turn Undead: Missing caster data or invalid roll result.");
    console.warn("Turn Undead Debug:", { casterToken, casterActor, rollResult: args.rollResult });
    return;
}

// 2. Define Constants
const RANGE_FEET = 30; // "Near"
const FLEE_ROUNDS = 5;

// 3. Wait a moment for the duration tracking to be set up by the system
await new Promise(resolve => setTimeout(resolve, 100));

// 4. Get the Turn Undead spell from the caster
const turnUndeadSpell = casterActor.items.find(i => i.name === "Turn Undead" && (i.type === "Spell" || i.type === "NPC Spell"));
if (!turnUndeadSpell) {
    ui.notifications.warn("Turn Undead: Could not find Turn Undead spell on caster.");
    return;
}

// 5. Get the active duration spell instance for Turn Undead
const activeDurationSpells = casterActor.getFlag("shadowdark-extras", "activeDurationSpells") || [];
let turnUndeadDuration = activeDurationSpells.find(d => d.spellId === turnUndeadSpell.id);

if (!turnUndeadDuration) {
    console.warn("Turn Undead: Duration tracking not found yet, will proceed without auto-cleanup.");
}

// 6. Find Targets
const targets = canvas.tokens.placeables.filter(t => {
    if (t.id === casterToken.id) return false;

    const dist = canvas.grid.measureDistance(casterToken, t);
    if (dist > RANGE_FEET) return false;

    const tActor = t.actor;
    if (!tActor) return false;

    const subtype = tActor.system?.subtype || "";
    const ancestry = tActor.system?.ancestry || "";
    const sdxType = tActor.flags?.["shadowdark-extras"]?.creatureType || "";

    const isUndead = (subtype.toLowerCase().includes("undead") ||
        ancestry.toLowerCase().includes("undead") ||
        sdxType.toLowerCase().includes("undead"));

    return isUndead;
});

if (targets.length === 0) {
    ui.notifications.info("Turn Undead: No undead creatures found within range.");
    return;
}

// 7. Process Each Target
let chatContent = `<h3>Turn Undead Results</h3>
<p><strong>Spellcasting Check:</strong> ${spellCheckResult}</p>
<hr>`;

for (const t of targets) {
    const tActor = t.actor;
    const tName = t.name;

    // Roll CHA check
    let chaTotal = NaN;

    try {
        const chaRoll = await tActor.rollAbility("cha", {
            fastForward: true,
            skipPrompt: true,
            chatMessage: false
        });

        // Extract the total from the Shadowdark roll structure
        // Structure: chaRoll.rolls.main.roll.total
        if (chaRoll?.rolls?.main?.roll?.total !== undefined) {
            chaTotal = Number(chaRoll.rolls.main.roll.total);
        } else if (chaRoll?.rolls?.main?.roll?._total !== undefined) {
            chaTotal = Number(chaRoll.rolls.main.roll._total);
        }
    } catch (e) {
        console.error(`Turn Undead: System roll failed for ${tName}`, e);
    }

    // If we still don't have a valid total, show error
    if (isNaN(chaTotal)) {
        console.error(`Turn Undead: Could not extract roll total for ${tName}`);
        chatContent += `<p><strong>${tName}:</strong> <span style="color:red">Error extracting CHA roll result</span></p>`;
        continue;
    }

    let resultMsg = "";

    if (chaTotal >= spellCheckResult) {
        resultMsg = `<span style="color:green"><strong>Resists</strong></span> (CHA Check: ${chaTotal} vs ${spellCheckResult})`;
    } else {
        const diff = spellCheckResult - chaTotal;
        const targetLevel = Number(tActor.system.level?.value ?? tActor.system.level ?? 0);
        const casterLevel = Number(casterActor.system.level?.value ?? casterActor.system.level ?? 0);

        if (diff >= 10 && targetLevel <= casterLevel) {
            resultMsg = `<span style="color:red"><strong>DESTROYED!</strong></span> (CHA Check: ${chaTotal}, Failed by ${diff}, Level ${targetLevel} ≤ ${casterLevel})`;
            await tActor.update({ "system.attributes.hp.value": 0 });
        } else {
            resultMsg = `<span style="color:orange"><strong>Flees</strong></span> for ${FLEE_ROUNDS} rounds (CHA Check: ${chaTotal}, Failed by ${diff})`;

            // Create Flee Effect Item
            const fleeEffectData = {
                name: "Condition: Fleeing",
                type: "Effect",
                img: "icons/magic/control/fear-fright-white.webp",
                system: {
                    description: "<p>This creature is fleeing from the caster of Turn Undead.</p>",
                    duration: {
                        type: "rounds",
                        value: String(FLEE_ROUNDS)
                    }
                },
                effects: [{
                    name: "Fleeing",
                    icon: "icons/magic/control/fear-fright-white.webp",
                    origin: casterActor.uuid,
                    transfer: true,
                    duration: {
                        rounds: FLEE_ROUNDS,
                        startRound: game.combat.round,
                        startTime: game.time.worldTime,
                        startTurn: game.combat.turn,
                        combat: game.combat.id
                    },
                    flags: {
                        core: {
                            statusId: "fleeing"
                        }
                    },
                    changes: []
                }]
            };

            try {
                const createdItems = await tActor.createEmbeddedDocuments("Item", [fleeEffectData]);
                const fleeEffectItemId = createdItems[0].id;

                // Link to duration spell if it exists
                if (turnUndeadDuration) {
                    const currentDurationSpells = casterActor.getFlag("shadowdark-extras", "activeDurationSpells") || [];
                    const durationIndex = currentDurationSpells.findIndex(d => d.instanceId === turnUndeadDuration.instanceId);

                    if (durationIndex >= 0) {
                        currentDurationSpells[durationIndex].targetEffects = currentDurationSpells[durationIndex].targetEffects || [];
                        currentDurationSpells[durationIndex].targetEffects.push({
                            targetActorId: tActor.id,
                            targetTokenId: t.id,
                            effectItemId: fleeEffectItemId,
                            targetName: tName
                        });

                        await casterActor.setFlag("shadowdark-extras", "activeDurationSpells", currentDurationSpells);
                    }
                }
            } catch (effectError) {
                console.error(`Turn Undead: Failed to create flee effect for ${tName}:`, effectError);
                resultMsg += ` <span style="color:red">(Effect creation failed)</span>`;
            }
        }
    }

    chatContent += `<p><strong>${tName}:</strong> ${resultMsg}</p>`;
}

ChatMessage.create({
    user: game.user.id,
    speaker: ChatMessage.getSpeaker({ token: casterToken }),
    content: chatContent
});

const caster = actor;
if (!caster) { 
    ui.notifications.warn("No caster found!"); 
    return; 
}
const sdxModule = game.modules.get("shadowdark-extras");
// Get critical success status from the spell cast context
const isCritical = args.isCritical ?? false;
const userId = (typeof originatingUserId !== "undefined") ? originatingUserId : undefined;
// Apply Wrath to ALL weapons (no dialog needed)
await sdxModule.api.applyWrathToAllWeapons(
    caster,                     // casterActor
    item,                       // casterItem
    userId,                     // originatingUserId
    isCritical                  // isCritical - grants +4/+2d8 on critical success
);

// Burning Hands / Flame Circle - Area of Effect Spell
// Trigger: onSuccess (also enable onCritical for critical hits)
// Deals 1d6 fire damage to all creatures within 5 feet
// Plays a burning hands animation toward EACH target!

if (!token) {
    ui.notifications.warn("Caster token not found!");
    return;
}

const isCritical = args.isCritical || false;
const gridSize = canvas.grid.size;
const range = 5; // feet
const rangePixels = (range / canvas.scene.dimensions.distance) * gridSize;

// Get caster center position
const casterX = token.center.x;
const casterY = token.center.y;

// Find all tokens within range (excluding the caster)
const nearbyTokens = canvas.tokens.placeables.filter(t => {
    if (t.id === token.id) return false; // Skip caster

    const distance = Math.hypot(t.center.x - casterX, t.center.y - casterY);
    return distance <= rangePixels + (t.document.width * gridSize / 2);
});

if (nearbyTokens.length === 0) {
    ui.notifications.info("No creatures within range!");
    return;
}

// Play JB2A burning hands toward EACH target
if (game.modules.get("sequencer")?.active) {
    let seq = new Sequence();

    for (const target of nearbyTokens) {
        seq.effect()
            .file("jb2a.burning_hands.01.orange")
            .atLocation(token)
            .stretchTo(target)
            .scale(0.5);
    }

    seq.play();
}

// Roll damage
let damageFormula = "1d6";
const damageRoll = await new Roll(damageFormula).evaluate();
let totalDamage = damageRoll.total;

// Double damage on critical
if (isCritical) {
    totalDamage *= 2;
}

// Apply damage to each nearby token
const hitNames = [];
for (const target of nearbyTokens) {
    const targetActor = target.actor;
    if (!targetActor) continue;

    const currentHP = targetActor.system.attributes.hp.value;
    const newHP = Math.max(0, currentHP - totalDamage);
    await targetActor.update({ "system.attributes.hp.value": newHP });
    hitNames.push(target.name);
}

// Create chat message
const critText = isCritical ? " <b>(CRITICAL - DOUBLE DAMAGE!)</b>" : "";
ChatMessage.create({
    content: `
        <div style="border: 2px solid #ff6600; border-radius: 8px; padding: 8px; background: linear-gradient(135deg, #2a1a0a 0%, #4a2a1a 100%);">
            <h3 style="color: #ff9900; margin: 0 0 4px 0;">🔥 Burning Hands${critText}</h3>
            <p style="margin: 4px 0; color: #ffcc66;">Damage: <b>${totalDamage}</b> fire</p>
            <p style="margin: 4px 0; color: #ffcc66;">Hit: ${hitNames.join(", ")}</p>
        </div>
    `,
    speaker: speaker
});

ui.notifications.info(`Burning Hands hit ${hitNames.length} creature(s) for ${totalDamage} damage!`);
